  getAllCustomers(): Promise<Customer[]>;
  getAllMessages(): Promise<Message[]>;
  getAllNotifications(): Promise<Notification[]>;
  getAllSystemLogs(): Promise<SystemLog[]>;
  getAllCustomerRatings(): Promise<CustomerRating[]>;
}

