    let result;
    if (teamMemberId) {
      result = await baseQuery
        .where(eq(pointsHistory.teamMemberId, teamMemberId))
        .limit(limit);
    } else {
      result = await baseQuery.limit(limit);
    }

    return result.map(row => ({
      ...row.points_history,
      teamMember: row.team_members,
      performedByUser: row.users || undefined
    }));
  }
  // Backup and Export implementations
  async getAllCustomers(): Promise<Customer[]> {
    return await db.select().from(customers);
  }

  async getAllMessages(): Promise<Message[]> {
    return await db.select().from(messages).orderBy(desc(messages.createdAt));
  }

  async getAllNotifications(): Promise<Notification[]> {
    return await db.select().from(notifications).orderBy(desc(notifications.createdAt));
  }

  async getAllSystemLogs(): Promise<SystemLog[]> {
    return await db.select().from(systemLogs).orderBy(desc(systemLogs.timestamp));
  }

  async getAllCustomerRatings(): Promise<CustomerRating[]> {
    return await db.select().from(customerRatings).orderBy(desc(customerRatings.createdAt));
  }

  // Delete user completely
  async deleteUser(id: string): Promise<boolean> {
    try {
      // First get the user to check if they have a linked team member
      const user = await this.getUser(id);
      if (!user) {
        return false;
      }

      // Find and delete associated team member if exists
      const teamMember = await this.getTeamMemberByUserId(id);
      if (teamMember) {
        await this.deleteTeamMember(teamMember.id);
      }

      // Delete user from database
      const result = await db.delete(users).where(eq(users.id, id));
      return result.rowCount ? result.rowCount > 0 : false;
    } catch (error) {
      console.error("Error deleting user:", error);
      return false;
    }
  }

  // Toggle user active status
  async toggleUserStatus(id: string): Promise<User | undefined> {
    try {
      // First get current user status
      const user = await this.getUser(id);
      if (!user) {
        return undefined;
      }

      // Toggle the status
      const newStatus = user.isActive === "true" ? "false" : "true";

      const [updated] = await db
        .update(users)
        .set({ isActive: newStatus })
        .where(eq(users.id, id))
        .returning();

      return updated;
    } catch (error) {
      console.error("Error toggling user status:", error);
      return undefined;
    }
  }
}

export const storage = new DatabaseStorage();

