  --sidebar-foreground: hsl(215 8% 13%);
  --sidebar-primary: hsl(207 90% 42%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(210 40% 96%);
  --sidebar-accent-foreground: hsl(215 8% 13%);
  --sidebar-border: hsl(214 32% 91%);
  --sidebar-ring: hsl(207 90% 42%);
  --font-sans: 'Roboto', 'Open Sans', sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: 0.5rem;
}

/* Responsive Base Styles */
* {
  box-sizing: border-box;
}

html {
  font-size: 16px;
  overflow-x: hidden;
}

body {
  margin: 0;
  padding: 0;
  min-height: 100vh;
  overflow-x: hidden;
  background: hsl(var(--background));
  background: hsl(var(--background));
}

/* Mobile-First Responsive Container */
.mobile-container {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  width: 100%;
  max-width: 100vw;
  overflow-x: hidden;
}

/* Responsive Breakpoints */
@media (min-width: 640px) {
  html {
    font-size: 17px;
  }

  .mobile-container {
    max-width: 640px;
    margin: 0 auto;
  }
}

@media (min-width: 768px) {
  html {
    font-size: 18px;
  }

  .mobile-container {
    max-width: 768px;
  }
}

@media (min-width: 1024px) {
  html {
    font-size: 19px;
  }

  .mobile-container {
    max-width: 1024px;
  }

  /* Desktop Layout Adjustments */
  .desktop-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 1rem;
  }

  .desktop-grid-3 {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 1rem;
  }
}

@media (min-width: 1280px) {
  html {
    font-size: 20px;
  }

  .mobile-container {
    max-width: 1280px;
  }

  /* Large Desktop Adjustments */
  .desktop-grid-4 {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 1.5rem;
  }
}

@media (min-width: 1536px) {
  .mobile-container {
    max-width: 1536px;
  }
}

/* Touch-Friendly Sizing */
@media (max-width: 640px) {
  .touch-target {
    min-height: 44px;
    min-width: 44px;
  }

  button, .button, input, select, textarea {
    min-height: 44px;
  }
}

/* Landscape Mobile Adjustments */
@media (orientation: landscape) and (max-height: 600px) {
  .mobile-container {
    padding-bottom: 0;
  }

  .landscape-adjust {
    padding: 0.5rem;
  }
}

/* High DPI Display Adjustments */
@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
  .crisp-text {
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }
}

/* Container Queries Support (when available) */
@container (min-width: 640px) {
  .responsive-card {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 1rem;
  }
}

/* Utility Classes for Responsive Design */
.responsive-padding {
  padding: 0.5rem;
}

@media (min-width: 640px) {
  .responsive-padding {
    padding: 1rem;
  }
}

@media (min-width: 1024px) {
  .responsive-padding {
    padding: 1.5rem;
  }
}

.responsive-text {
  font-size: 0.875rem;
}

@media (min-width: 640px) {
  .responsive-text {
    font-size: 1rem;
  }
}

@media (min-width: 1024px) {
  .responsive-text {
    font-size: 1.125rem;
  }
}

/* Smooth Transitions for Responsive Changes */
* {
  transition: font-size 0.2s ease, padding 0.2s ease, margin 0.2s ease;
}

