@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
    font-family: 'Roboto', var(--font-sans);
  }

  /* Arabic RTL styling */
  [dir="rtl"] {
    text-align: right;
  }

  [dir="rtl"] * {
    direction: rtl;
  }

  /* Fix flex direction for RTL */
  [dir="rtl"] .flex:not(.flex-col):not(.flex-row) {
    flex-direction: row-reverse;
  }

  [dir="rtl"] .justify-between {
    flex-direction: row-reverse;
  }

  /* Fix spacing for RTL */
  [dir="rtl"] .space-x-1 > :not([hidden]) ~ :not([hidden]),
  [dir="rtl"] .space-x-2 > :not([hidden]) ~ :not([hidden]),
  [dir="rtl"] .space-x-3 > :not([hidden]) ~ :not([hidden]),
  [dir="rtl"] .space-x-4 > :not([hidden]) ~ :not([hidden]) {
    --tw-space-x-reverse: 1;
    margin-right: var(--tw-space-x-reverse) * var(--tw-space-x);
    margin-left: calc(1 - var(--tw-space-x-reverse)) * var(--tw-space-x);
  }

  /* RTL text alignment */
  [dir="rtl"] .text-left {
    text-align: right;
  }

  [dir="rtl"] .text-right {
    text-align: left;
  }

  /* RTL positioning for floating elements */
  [dir="rtl"] .fixed.right-4 {
    right: auto;
    left: 1rem;
  }

  [dir="rtl"] .fixed.left-1\/2 {
    left: auto;
    right: 50%;
    transform: translateX(50%);
  }

  /* RTL adjustments for buttons and form elements */
  [dir="rtl"] .ml-2 {
    margin-left: 0;
    margin-right: 0.5rem;
  }

  [dir="rtl"] .ml-3 {
    margin-left: 0;
    margin-right: 0.75rem;
  }

  [dir="rtl"] .mr-2 {
    margin-right: 0;
    margin-left: 0.5rem;
  }

  [dir="rtl"] .mr-3 {
    margin-right: 0;
    margin-left: 0.75rem;
  }

  /* RTL grid positioning */
  [dir="rtl"] .grid {
    direction: rtl;
  }

  /* RTL card layouts */
  [dir="rtl"] .task-card,
  [dir="rtl"] .stat-card {
    text-align: right;
  }

  /* Stat card responsive styling */
  .stat-card {
    @apply bg-card rounded-lg p-3 sm:p-4 lg:p-5 shadow-sm border border-surface-variant;
    min-height: 80px;
    display: flex;
    align-items: center;
    transition: all 0.2s ease;
  }

  .stat-card:hover {
    @apply shadow-md;
    transform: translateY(-1px);
  }

  @media (min-width: 640px) {
    .stat-card {
      min-height: 96px;
    }
  }

  @media (min-width: 1024px) {
    .stat-card {
      min-height: 112px;
    }
  }

  /* Safe area adjustments for modern devices */
  .safe-area-bottom {
    padding-bottom: env(safe-area-inset-bottom);
  }

  .safe-area-top {
    padding-top: env(safe-area-inset-top);
  }

  /* Enhanced animations for scheduler */
  @keyframes fadeInUp {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes slideInRight {
    from {
      opacity: 0;
      transform: translateX(20px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  /* Scheduler specific animations */
  [data-testid^="scheduler-task-"] {
    animation: fadeInUp 0.4s ease-out forwards;
  }

  [data-testid^="upcoming-task-"] {
    animation: slideInRight 0.5s ease-out forwards;
  }

  /* Smooth hover transitions for cards */
  .group:hover {
    transition: all 0.2s ease;
  }

  /* Custom gradient backgrounds */
  .gradient-primary {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--secondary)) 100%);
  }

  .gradient-muted {
    background: linear-gradient(135deg, hsl(var(--muted)) 0%, hsl(var(--background)) 100%);
  }

  /* Line clamping utility */
  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  .line-clamp-3 {
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  /* RTL navigation adjustments */
  [dir="rtl"] .bottom-navigation {
    direction: rtl;
  }

  /* RTL form inputs */
  [dir="rtl"] input,
  [dir="rtl"] textarea,
  [dir="rtl"] select {
    text-align: right;
  }

  [dir="rtl"] input[type="date"],
  [dir="rtl"] input[type="time"],
  [dir="rtl"] input[type="number"] {
    text-align: left; /* Keep these left-aligned for clarity */
  }

  html {
    scroll-behavior: smooth;
  }

  /* Mobile optimizations */
  @media (max-width: 768px) {
    body {
      -webkit-text-size-adjust: 100%;
      -webkit-tap-highlight-color: transparent;
      touch-action: manipulation;
    }
  }
}

@layer components {
  .mobile-container {
    @apply min-h-screen flex flex-col max-w-md mx-auto bg-surface;
  }

  .stat-card {
    @apply bg-white rounded-xl p-4 shadow-lg border-0 transition-all duration-300 hover:shadow-xl hover:scale-105;
    background: linear-gradient(135deg, hsl(var(--surface)) 0%, hsl(var(--muted)) 100%);
    border: 1px solid hsl(var(--border) / 0.2);
    min-height: 110px;
    position: relative;
    overflow: hidden;
  }

  .stat-card::before {
    content: '';
