  }

  .availability-available {
    @apply bg-success;
  }

  .availability-busy {
    @apply bg-warning;
  }

  .availability-offline {
    @apply bg-surface-variant;
  }
}

@layer utilities {
  .touch-manipulation {
    touch-action: manipulation;
  }
}

/* Scroll to top utility classes for all containers */
[data-scroll-container],
[role="dialog"],
.overflow-auto,
.overflow-y-auto,
