  scroll-behavior: smooth;
}

/* Reset scroll position for new routes */
.route-transition {
  animation: fadeInFromTop 0.2s ease-in-out;
}

@keyframes fadeInFromTop {
  from {
    opacity: 0.95;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Compact UI Styles for Smaller Interface */
.compact-ui {
  font-size: 0.875rem; /* 14px base */
}

.compact-card {
  @apply p-2 rounded-lg border;
  min-height: 2.5rem;
