  line-height: 1.2;
}

.compact-stats .stat-card p:last-child {
  font-size: 0.75rem; /* 12px */
  font-weight: 600;
}

.compact-member-card {
  @apply p-2 rounded border;
  min-height: 2.5rem;
}

.compact-member-card .avatar {
  @apply w-6 h-6 text-xs;
}

.compact-section {
  @apply p-2 mb-2 rounded-lg;
}

.compact-title {
  @apply text-sm font-medium mb-1;
}

.compact-btn {
  @apply px-2 py-1 text-xs h-6 rounded;
}

.compact-spacing {
  @apply space-y-1 gap-1;
}

/* Mobile compact adjustments */
@media (max-width: 640px) {
  .compact-stats {
    grid-template-columns: repeat(3, 1fr);
  }

  .compact-card {
    padding: 0.375rem;
  }
}

